import React, { Component } from 'react';
import './App.css';
import Data from './data.json'


export default class App extends Component {

constructor(props){
  super(props)
  this.state={
    data : Data.data,
    keyword:"",
    results: [],
    showSuggestion :false,
    showDeatils:false
  }
}
//matchName
matchName=(name,keyword)=>{
  let keyLen = keyword.length
   keyword =keyword.toLowerCase()
   name =name.toLowerCase().substring(0,keyLen)
  return name === keyword && keyLen!==0 
}

//onSearch
onSearch=(value)=>{
  let results = this.state.data.filter((item)=>{
    this.matchname(item.name,value)
  })
  this.setState({
    results:results
  })
  console.log(results);
}

//updateField
updateField = (value)=>{
this.setState(
 { 
  keyword:value,
  showSuggestion:true }
)
this.onSearch(value)
}

  render() {
    return (
<div className='App'>
    <div className='autoComplete-Container'>
      <h1 className='main-head'>Employee Details</h1>
      <br/>
      <br/>
      <div className='search-container'>
              <div className='input'>
              <input className='search-bar' onChange={(e)=>this.updateField(e.target.value)} value={this.state.keyword} placeholder='Search..' type='text'/>
             <h1>{this.state.keyword}</h1>
              </div>
      </div>
</div>
</div>
)
}
}
